import React, { useState } from "react";

const [strike, setStrike] = useState(false);

function handleClick() {
    setStrike(true);

}

function ToDoItem(props) {

    return (
       <li>
       style= {{ textDecoration: strike ? "line-through" : null}}
       onClick={handleClick}
       {props.text}
       </li>
    )
};


export default ToDoItem;
