import React, { useState } from "react";

const toDoItems = [];

function App() {

  const [todoValue, setTodoValue] = useState("");

  function handleClick (event) {
   const { value } = event.target;

    setTodoValue(prevValue => {
      return [
        ...prevValue,
        value
      ]
    })
  }




  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form" onSubmit={handleClick}>
        <input type="text" value={todoValue} name="item"/> 
        <button type="submit">
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
          <li>A Item </li>
        </ul>
      </div>
    </div>
  );
}

export default App;
