import React, { useState } from "react";

function App() {

  const [item, setItem] = useState({});

  function handleChange(event) {
   const { value } = event.target;

     setItem (prevValue => {
      return {
        ...prevValue,
        [value]: item
      };
    });
  }



  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input type="text" onChange={handleChange} value={item} placeholder="add item" name="item"/> 
        <button >
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
          <li>A Item </li>
        </ul>
      </div>
    </div>
  );
}

export default App;
